const OpCode = require("../OpCode");

const jumpOpList = [OpCode.JUMP];
const conditionJumpOpList = [OpCode.JUMP_IF_FALSE, OpCode.JUMP_IF_NOT_POSITIVE];
const stack = [];

function optimize(codeList)
{
	runPass(codeList, jumpOpList, optimizeJump);
	runPass(codeList, conditionJumpOpList, optimizeConditionJump);
}

function runPass(codeList, opList, handler)
{
	for(var i=0, n=codeList.length; i<n; ++i){
		if(opList.indexOf(codeList[i][0]) >= 0){
			handler(codeList, i);
		}
	}
}

function optimizeConditionJump(codeList, index)
{
	var code = codeList[index];
	var jumpToCode = codeList[index+code[1]];
	if(jumpToCode != null && jumpToCode[0] == OpCode.JUMP){
		code[1] += jumpToCode[1];
	}
}

function optimizeJump(codeList, index)
{
	var realCount = getFinalJumpCount(codeList, index);
	var code = codeList[index];
	if(code[1] != realCount){
		code[1] = realCount;
	}
}

function getFinalJumpCount(codeList, index)
{
	stack.length = 0;
	var count = 0;
	for(;;){
		var code = codeList[index+count];
		if(code == null || code[0] != OpCode.JUMP){
			break;
		}
		if(code[1] == 0){
			return 0;
		}
		count += code[1];
		if(stack.indexOf(count) >= 0){
			return 0;
		}
		stack.push(count);
	}
	return count;
}

exports.optimize = optimize;